// App.js
import React from "react";
import { useSelector, useDispatch } from 'react-redux';
import { changeBlue, changeRed } from './slice';

function App() {
  const change = useSelector((state) => state.color.color); // 状態を取得
  const dispatch = useDispatch(); // アクションをディスパッチ

  return (
    
      <div className="back-ground" style={{backgroundColor:change}}>
        <button onClick={() => dispatch(changeBlue(追加データ))}>
          アクション1
        </button>
        <button onClick={() => dispatch(changeRed())}>
          アクション2
        </button>

      </div>
   
  );
}

export default App;
