// slice.js
import { createSlice } from "@reduxjs/toolkit";

const colorChanger = createSlice({
  name: "color", // スライスの名前
  initialState: {
    color: "white", // 初期状態
  },
  reducers: {
    blue: (state) => {
      state.color = "blue";
    },
    red: (状態) => {
      state.color = "red";
    },
  },
});

export const { アクション名1, アクション名2 } = colorChanger.actions; // アクションのエクスポート
export default colorChanger.reducer; // リデューサーのエクスポート
