// slice.js
import { createSlice } from '@reduxjs/toolkit';

const colorChanger = createSlice({
  name: 'スライス名', // スライスの名前
  initialState: {
    初期状態キー: 初期値, // 初期状態
    他の初期状態キー: 他の初期値,
  },
  reducers: {
    アクション名1: (状態, アクション) => {
      // 状態を変更するロジック
      状態.初期状態キー += アクション.payload; // 例: 状態の更新
    },
    アクション名2: (状態) => {
      // 状態を変更するロジック
      状態.他の初期状態キー = '新しい値'; // 例: 状態の変更
    },
    // 他のリデューサーも追加可能
  },
});

export const { アクション名1, アクション名2 } = スライス名.actions; // アクションのエクスポート
export default スライス名.reducer; // リデューサーのエクスポート
