// slice.js
import { createSlice } from "@reduxjs/toolkit";

const colorChanger = createSlice({
  name: "color", 
  initialState: {
    color: "white", 
  },
  reducers: {
    changeBlue: (state) => {
      state.color = "blue";
    },
    changeRed: (state) => {
      state.color = "red";
    },
  },
});

export const { changeBlue, changeRed } = colorChanger.actions; // アクションのエクスポート
export default colorChanger.reducer; // リデューサーのエクスポート
