// slice.js
import { createSlice } from '@reduxjs/toolkit';

const counter = createSlice({
  name: 'counter', // スライスの名前
  initialState: {
    count: 0, // 初期状態
  },
  reducers: {
    countUp: (state, action) => {
        state.count += action.payload; // 例: 状態の更新
    },
    countDown: (num) => {
        state.count -= num; // 例: 状態の更新
      },
    // 他のリデューサーも追加可能
  },
});

export const { countUp, countDown } = counter.actions; // アクションのエクスポート
export default counter.reducer; // リデューサーのエクスポート
