// src/App.js

import React, { useState } from 'react';
import Calculation from './components/calculation';
import Add from './components/add';
import UserName from './components/user-name';
import List from './components/list';
import Task from './components/task';


function App() {
  const [showAdd, setShowAdd] = useState(true);
  const [showCalculation, setShowCalculation] = useState(true);
  const [showUserName, setShowUserName] = useState(true);
  const [showList, setShowList] = useState(true);
  const [showTask, setShowTask] = useState(true);

  return (
    <div>
      <h1>useStateの練習</h1>
      
      <button onClick={() => setShowAdd(!showAdd)}>Add {showAdd ? 'Hide' : 'Show'}</button>
      <button onClick={() => setShowCalculation(!showCalculation)}>Calculation {showCalculation ? 'Hide' : 'Show'}</button>
      <button onClick={() => setShowUserName(!showUserName)}>UserName {showUserName ? 'Hide' : 'Show'}</button>
      <button onClick={() => setShowList(!showList)}>List {showList ? 'Hide' : 'Show'}</button>
      <button onClick={() => setShowTask(!showTask)}>Task {showTask ? 'Hide' : 'Show'}</button>


      
      {showAdd && <Add />}
      
      {showCalculation && <Calculation />}
      
      {showUserName && <UserName />}
      
      {showList && <List />}
      
      {showTask && <Task />}
    </div>
  );
}

export default App;
