import { useState } from "react";

const List = () =>{
    
    const [list,settask] = useState([]);
    
    return(
        <div>
            <h2>リストを追加して表示</h2>
            <h3>{list.map((task)=>{
                return <li>{task}</li>
            })} </h3>
            
            <input type="submit" value="追加" onClick={()=>{settask(task.push(""))}} />
        </div>
    )
}
export default List;
