// slice.js
import { createSlice } from '@reduxjs/toolkit';

const colorChanger = createSlice({
  name: 'color', // スライスの名前
  initialState: {
    color: "white", // 初期状態
  },
  reducers: {
    blue: (state) => {
      // 状態を変更するロジック
      状態.初期状態キー += アクション.payload; // 例: 状態の更新
    },
    red: (状態) => {
      // 状態を変更するロジック
      状態.他の初期状態キー = '新しい値'; // 例: 状態の変更
    },
    // 他のリデューサーも追加可能
  },
});

export const { アクション名1, アクション名2 } = colorChanger.actions; // アクションのエクスポート
export default colorChanger.reducer; // リデューサーのエクスポート
