// App.js
import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { countUp, countDown } from './slice';

function App() {
  const count = useSelector((state) => state.counter.count); // 状態を取得
  const dispatch = useDispatch(); // アクションをディスパッチ
  const handleChange = (e)=>{
    const inputNum = e.target.value;
}

  return (
    <div>
      <h1>count: {count}</h1>
      <input type="number" onChange={handleChange(e)} />
      <button onClick={() => dispatch(countUp(inputNum))}>+</button>
      <button onClick={() => dispatch(countDown(inputNum))}>-</button>
      
    </div>
  );
}

export default App;
