import { useState } from "react";

const Task = () =>{
    
    const [tasklist,settasklist] = useState([]);
    const [task,settask] = useState("")

    return(
        <div>
            <p>{task}</p>
            <h2>リストに追加して表示するだけ</h2>
            <input type="text" placeholder="何か入力してね" onChange={(e)=>{settask(e.target.value)}} />
            <input type="submit" onClick={()=>{settasklist([...tasklist,task])}}/>
            
            <h3>{tasklist}</h3>
            <ol>{tasklist.map((task)=>{
                return <li>{task}</li>
            })} </ol>
        </div>
    )
}
export default Task;
